import React, { useState, useEffect } from 'react';

const getRandomHue = () => Math.floor(Math.random() * 360);

const getRandomNum = () => Math.floor(Math.random() * 100);

const formatHsl = (h, s, l) => {
  return `hsl(${h}, ${s}%, ${l}%)`;
};

const HarmonyGenerator = () => {
  const [primaryHue, setPrimaryHue] = useState(0);
  const [primarySat, setPrimarySat] = useState(0);
  const [primaryLum, setPrimaryLum] = useState(0);
  const [primaryColor, setPrimaryColor] = useState(
    formatHsl(primaryHue, primarySat, primaryLum)
  );

  useEffect(() => {
    setPrimaryHue(getRandomHue());
    setPrimarySat(getRandomNum());
    setPrimaryLum(getRandomNum());
    setPrimaryColor(formatHsl(primaryHue, primarySat, primaryLum));
  }, []);

  return (
    <div>
      <div
        className='primary-color-preview'
        style={{ backgroundColor: primaryColor }}
      ></div>
      <form>
        <div className='input-wrapper'>
          <p className='input-wrapper__prop'>Hue:</p>
          <input
            type='range'
            min='0'
            max='360'
            value={primaryHue}
            onChange={e => setPrimaryHue(e.target.value)}
          />
          <p className='input-wrapper__val'>{primaryHue}</p>
        </div>
      </form>
    </div>
  );
};

export default HarmonyGenerator;
