import React from 'react';
import { hslToHex, hslToRgb } from '../utils/colorConverter';

const isDark = rgbArr => rgbArr.every(val => val < 180);

const HarmonyColorCell = ({ color }) => {
  const hex = hslToHex(color.h, color.s, color.l);
  const rgb = hslToRgb(color.h, color.s, color.l);
  const textStyle = {
    color: isDark([rgb.r, rgb.g, rgb.b]) ? 'white' : 'black',
  };

  return (
    <div className='harmony-color-cell' style={{ backgroundColor: hex }}>
      <p style={{ color: textStyle }}>{hex}</p>
    </div>
  );
};

export default HarmonyColorCell;
