import axios from 'axios';
import namedColors from 'color-name-list';
import nearestColor from 'nearest-color';
import generateRandomHsl from './generateRandomHsl';
import { hslToHex } from './colorConverter';

const generateColor = async () => {
  /* const name = namedColors.find(color => color.hex === '6B0504');
  console.log(name);
  const nearestName = nearestColor.from({
    rgb: { r: 255, g: 0, b: 0 },
  });
  console.log(nearestName); */

  const hsl = generateRandomHsl();
  const hex = hslToHex(hsl.h, hsl.s, hsl.l).replace('#', '');

  /* const res = await axios.get(`https://api.color.pizza/v1/${hex}`);
  const name = await res.data.colors[0].name; */

  const res = await namedColors.find(color => color.hex === hex);
  const name = await res;

  console.log(name);
};

export default generateColor;
