import namedColors from 'color-name-list';
import nearestColor from 'nearest-color';

const generateColor = () => {
  const name = namedColors.find(color => color.hex === '#ffffff');
  console.log(name);
  const nearestName = nearestColor.from({ [name]: name, hex: '#ffffff' });
  console.log(nearestName);
};

export default generateColor;
