import React, { useState } from 'react';
import generateColor from '../utils/generateColor';
import ColorCell from './ColorCell';

const initState = [
  {
    num: 1,
    color: async () => await generateColor(),
    locked: false,
  },
  {
    num: 2,
    color: generateColor(),
    locked: false,
  },
  {
    num: 3,
    color: generateColor(),
    locked: false,
  },
  {
    num: 4,
    color: generateColor(),
    locked: false,
  },
  {
    num: 5,
    color: generateColor(),
    locked: false,
  },
];

const PaletteGenerator = () => {
  const [palette, setPalette] = useState(initState);

  const generateColors = () => {
    setPalette(
      palette.map(async color => {
        if (!color.locked) {
          color.color = await generateColor();
          console.log(color);
          return color;
        }
        return color;
      })
    );
  };

  const toggleLocked = num => {
    setPalette(
      palette.map(color => {
        if (color.num === num) {
          color.locked = !color.locked;
          return color;
        }
        return color;
      })
    );
  };

  return (
    <section className='palette-generator'>
      <div className='palette-grid'>
        {palette.map(color => (
          <ColorCell
            color={color}
            toggleLocked={toggleLocked}
            key={color.num}
          />
        ))}
      </div>
      <button className='btn generate-btn' onClick={() => generateColors()}>
        Generate
      </button>
    </section>
  );
};

export default PaletteGenerator;
